/**
 * Only allows landmarks for the left hand to pass through
 */
const processEvent = async (targetPort, sourceWidget, event) => {
  const data = event.data.value;
  const handedness = data?.handedness;

  if(handedness.length){

    const handIndex = handedness.findIndex((hand) => hand[0]?.categoryName === 'Right');
    if(handIndex !== -1){
      return sendToPort('right-hand', {
    		type: DataType.JsonObj,
    		// Recreate the event with only the filtered data
    		value: {
    		  image: data.image,
    		  handedness: data.handedness[handIndex],
    		  landmarks: data.landmarks[handIndex],
    		  worldLandmarks: data.worldLandmarks[handIndex],
    		}
    	});   
    }
  }
}
